module.exports = AttribDirective;

var Attrib = require('src/ast/attrib')
;

/**
 * An attribute based directive
 * @class AttribDirective
 * @extends Attrib
 * @param  {Object} opts - Attribute options
 */
var AttribDirective = function(opts)
{
  if(!(this instanceof AttribDirective))
    return new AttribDirective(opts);

  Attrib.call(this, opts);

  /** @property @override {string} AttribDirective#data */
}
AttribDirective.prototype = Object.create(Attrib.prototype);
