var InterpolatorChunk = require('src/ast/interpolator-chunk');

/**
 * @class InterpolatorExpression
 * @extends InterpolatorChunk
 * @param {Object} opts - The interpolator expression options
 */
function InterpolatorExpression(opts)
{
  if(!(this instanceof InterpolatorExpression))
    return new InterpolatorExpression(opts);

  InterpolatorChunk.call(this, opts);
}
InterpolatorExpression.prototype = Object.create(InterpolatorChunk.prototype)
