 var Node = require('src/ast/node')
;

/**
 * A DOM tag
 * @class Tag
 * @extends Node
 * @param  {Object} opts - The tag options
 */
var Tag = module.exports = function Tag(opts)
{
  if(!(this instanceof Tag))
    return new Tag(opts);

  Node.call(this, opts);
}

Tag.prototype = Object.create(Node.prototype);
