Template.assemble = function(prefix, node)
{
  var attribs = {}
    , prefix = prefix || ''
  ;
  node.attribs.forEach(function(a){
    attribs[a.name] = a.data;
  });
  var context = attribs.context
    , name = attribs.name
  ;

  var start = prefix+'("'+name+'", function('+context+'){';
    // content
  var end = '})';

  return {start:start, end:end};
}
