
var DirectiveRegistery = module.exports = function()
{
  this.tagDirectives = {};
  this.attribDirectives = {};
}

DirectiveRegistery.prototype.registerTagDirective = function (name, def) {
  var assemble = def.assemble.bind(def, '__MC__.'+name);
  var fn = def.fn;

  this.tagDirectives[name] = {assemble: assemble, fn: fn};
};

DirectiveRegistery.prototype.getTagAssemble = function (name) {
  return this.tagDirectives[name].assemble;
};
