module.exports = DomHandler;

var DomHandler = function(stream)
{
  if(!(this instanceof DomHandler))
    return new DomHandler(stream);

  /** @property {MCNode[]} DomHandler#ast */
  this.ast = [];

  /** @property {Stream} DomHandler#_stream */
  this._stream = stream;

  /** @property {Boolean} DomHandler#_done */
  this._done = false;

  /** @property {MCTag[]} DomHandler#_tagStack */
  this._tagStack = [];

  /** @property {HtmlParser} DomHandler#_parser */
  this._parser = this._parser || null;
}
