var _ = require('lodash')
  , Utils = require('src/utils')
;

module.exports = (function(){

  /**
   * @class MCCompilerDirective
   * @param {string} data - The raw data to be included in the MCCompilerDirective
   */
  function MCCompilerDirective(data)
  {
    /** @member {{string}} MCCompilerDirective#data */
    this.data = data;

    /** @member {{string}} MCCompilerDirective#value */
    this.value = MCCompilerDirective.getValue(data);
  }

  /**
   * Gets the JS safe value from raw data
   * @function MCCompilerDirective.getValue
   * @param  {string} data - The raw data
   * @return {string} The JS safe value
   *
   * @TODO Write unit test
   */
  MCCompilerDirective.getValue = function(data)
  {
    return data.replace(/^\s*@\s*/, '');
  }

  return MCCompilerDirective;

})();
